import React, { useState } from 'react';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { doc, setDoc } from 'firebase/firestore';
import { auth, db } from '../firebase';
import { useNavigate } from 'react-router-dom';
import './ArtistAuthForm.css';

const ArtistAuthForm = ({ onClose }) => {
  const navigate = useNavigate();

  // Form data state
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    location: '',
    phone: '',
    instagram: ''
  });

  // Categories state
  const [selectedCategories, setSelectedCategories] = useState([]);

  // UI feedback states
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Predefined artist categories
  const artistCategories = [
    'Painter',
    'Sculptor',
    'Digital Artist',
    'Photographer',
    'Illustrator',
    'Mixed Media',
    'Street Artist',
    'Ceramic Artist',
    'Textile Artist',
    'Performance Artist',
    'Other'
  ];

  // Handle text input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle category selection
  const handleCategoryToggle = (category) => {
    setSelectedCategories(prev => {
      if (prev.includes(category)) {
        return prev.filter(cat => cat !== category);
      } else {
        return [...prev, category];
      }
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Set loading state and clear errors
    setLoading(true);
    setError('');

    try {
      // Validation: Check if at least one category is selected
      if (selectedCategories.length === 0) {
        throw new Error('Please select at least one artist category');
      }

      // Create user with Firebase Authentication
      const userCredential = await createUserWithEmailAndPassword(
        auth,
        formData.email,
        formData.password
      );

      const user = userCredential.user;

      // Store artist data in Firestore
      await setDoc(doc(db, 'artists', user.uid), {
        uid: user.uid,
        name: formData.name,
        email: formData.email,
        location: formData.location,
        phone: formData.phone || '',
        instagram: formData.instagram || '',
        categories: selectedCategories,
        createdAt: new Date().toISOString(),
        profileComplete: true
      });

      // Redirect to artist dashboard
      navigate('/artist-dashboard');

    } catch (error) {
      console.error('Error creating artist account:', error);
      setError(error.message || 'An error occurred while creating your account');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="artist-form-backdrop" onClick={onClose}>
      <div className="artist-form-content" onClick={(e) => e.stopPropagation()}>
        {/* Close button */}
        <button className="close-button" onClick={onClose}>
          Ã—
        </button>

        {/* Form */}
        <form onSubmit={handleSubmit} className="artist-form">
          <h2 className="form-title">Join Our Artist Community</h2>
          <p className="form-subtitle">
            Create your artist profile and start showcasing your work to the world
          </p>

          {/* Error display */}
          {error && (
            <div className="error-message">
              {error}
            </div>
          )}

          {/* Form fields */}
          <div className="form-fields">
            <div className="input-group">
              <label htmlFor="name">Full Name *</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                required
                placeholder="Enter your full name"
              />
            </div>

            <div className="input-group">
              <label htmlFor="email">Email Address *</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                required
                placeholder="Enter your email address"
              />
            </div>

            <div className="input-group">
              <label htmlFor="password">Password *</label>
              <input
                type="password"
                id="password"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                required
                placeholder="Create a secure password"
                minLength="6"
              />
            </div>

            <div className="input-group">
              <label htmlFor="location">Location *</label>
              <input
                type="text"
                id="location"
                name="location"
                value={formData.location}
                onChange={handleInputChange}
                required
                placeholder="City, Country"
              />
            </div>

            <div className="input-group">
              <label htmlFor="phone">Phone Number</label>
              <input
                type="tel"
                id="phone"
                name="phone"
                value={formData.phone}
                onChange={handleInputChange}
                placeholder="Optional - Enter your phone number"
              />
            </div>

            <div className="input-group">
              <label htmlFor="instagram">Instagram Username</label>
              <input
                type="text"
                id="instagram"
                name="instagram"
                value={formData.instagram}
                onChange={handleInputChange}
                placeholder="Optional - Your Instagram handle"
              />
            </div>
          </div>

          {/* Artist Categories */}
          <div className="categories-section">
            <label className="categories-label">What type of artist are you? *</label>
            <div className="categories-grid">
              {artistCategories.map((category) => (
                <button
                  key={category}
                  type="button"
                  className={`category-button ${selectedCategories.includes(category) ? 'selected' : ''}`}
                  onClick={() => handleCategoryToggle(category)}
                >
                  {category}
                </button>
              ))}
            </div>
          </div>

          {/* Submit button */}
          <button
            type="submit"
            className="submit-button"
            disabled={loading}
          >
            {loading ? 'Creating Account...' : 'Create Account'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default ArtistAuthForm;
