import React, { useState, useEffect } from 'react';
import ArtistAuthForm from './ArtistAuthForm';
import { db } from '../firebase';
import { doc, setDoc, getDoc } from 'firebase/firestore';
import './Homepage.css';

const Homepage = () => {
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [showArtistForm, setShowArtistForm] = useState(false);

  // Track cursor position
  useEffect(() => {
    const handleMouseMove = (e) => {
      setCursorPosition({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);

    // Cleanup function to remove event listener
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  // Handle main button click
  const handleBeginJourney = () => {
    setIsModalOpen(true);
  };

  // Handle modal option clicks
  const handleExploreArts = () => {
    console.log('User chose: Explore the Arts');
    setIsModalOpen(false);
    // Future routing logic will go here
  };

  const handleIAmArtist = () => {
    console.log('User chose: I am an Artist');
    setIsModalOpen(false);
    setShowArtistForm(true);
  };

  // Handle closing artist form
  const handleCloseArtistForm = () => {
    setShowArtistForm(false);
  };

  // Test Firebase connectivity
  const testFirebase = async () => {
    try {
      console.log('Testing Firebase connectivity...');
      const testData = {
        test: true,
        timestamp: new Date().toISOString(),
        message: 'Firebase test from homepage'
      };

      await setDoc(doc(db, 'test', 'homepage-test'), testData);
      console.log('✅ Firebase test successful - data written');

      const docSnap = await getDoc(doc(db, 'test', 'homepage-test'));
      if (docSnap.exists()) {
        console.log('✅ Firebase test successful - data read:', docSnap.data());
        alert('Firebase test successful! Check console for details.');
      }
    } catch (error) {
      console.error('❌ Firebase test failed:', error);
      alert('Firebase test failed: ' + error.message);
    }
  };

  return (
    <div className="homepage-container">
      {/* Spotlight Overlay */}
      <div 
        className="spotlight-overlay"
        style={{
          '--x': `${cursorPosition.x}px`,
          '--y': `${cursorPosition.y}px`
        }}
      />

      {/* Content Wrapper */}
      <div className="content-wrapper">
        <button 
          className="main-cta-button"
          onClick={handleBeginJourney}
        >
          Begin Your Journey
        </button>

        {/* Debug: Firebase Test Button */}
        <button 
          className="main-cta-button"
          onClick={testFirebase}
          style={{ marginTop: '20px', fontSize: '0.9rem', padding: '10px 20px' }}
        >
          Test Firebase
        </button>
      </div>

      {/* Modal */}
      {isModalOpen && (
        <div className="modal-backdrop" onClick={() => setIsModalOpen(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h2 className="modal-title">Art-Artists </h2>
            <p className="modal-subtitle">How would you like to begin?</p>
            <div className="modal-buttons">
              <button 
                className="modal-option-button"
                onClick={handleExploreArts}
              >
                Explore the Arts
              </button>
              <button 
                className="modal-option-button"
                onClick={handleIAmArtist}
              >
                I am an Artist
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Artist Auth Form */}
      {showArtistForm && (
        <ArtistAuthForm onClose={handleCloseArtistForm} />
      )}
    </div>
  );
};

export default Homepage;
